local SmashSoundtrackABP = RegisterMod("Super Smash Bros. Soundtrack [AB+ Ver.]", 1)function SmashSoundtrackABP:onStart()	if SoundtrackSongList then		AddSoundtrackToMenu("SuperSmashBros")	else		Isaac.ConsoleOutput("The Soundtrack Menu Mod is required for Super Smash Bros. Soundtrack")	endendSmashSoundtrackABP:AddCallback(ModCallbacks.MC_POST_GAME_STARTED, SmashSoundtrackABP.onStart);